// src/components/EmailSummary.js

import React from 'react';

const EmailSummary = ({ email }) => {
    return (
        <div className="email-summary p-4 border border-gray-300 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold">{email.Snippet}</h3>
            <h3 className="text-lg font-semibold mt-2">{email.Subject}</h3>
            <p className="mt-2"><strong className="font-semibold">From:</strong> {email.Sender}</p>
            <p className="mt-1"><strong className="font-semibold">Date:</strong> {email.Date}</p>
            <p className="mt-2"><strong className="font-semibold"></strong> {email.Summary}</p>
            
        </div>
    );
};

export default EmailSummary;
